function convertHexToRGB(hexString){var rgbArr=[];if(3===hexString.length)(rgbArr=hexString.split("")).forEach((element,index,arr)=>{arr[index]=element+""+element});else if(6===hexString.length)for(var i=0;i<6;i+=2)rgbArr.push(hexString.substring(i,i+2));var rgb={};return rgb.red=parseInt(rgbArr[0],16),rgb.green=parseInt(rgbArr[1],16),rgb.blue=parseInt(rgbArr[2],16),rgb}function convertRGBToHex(red,green,blue){var hex1,hex2,hex3=0;return(hex1=red.toString(16)).length<2&&(hex1=hex1+""+hex1),(hex2=green.toString(16)).length<2&&(hex2=hex2+""+hex2),(hex3=blue.toString(16)).length<2&&(hex3=hex3+""+hex3),hex1+""+hex2+hex3}document.addEventListener("click",e=>{if("convertHex"==e.target.id){const input=document.querySelector("#hexInput");if(input.value=input.value.replace(/[^A-Za-z0-9]+/g,""),3===input.value.length||6===input.value.length){const rgb=convertHexToRGB(input.value);document.querySelector("#redRGBInput").value=rgb.red,document.querySelector("#greenRGBInput").value=rgb.green,document.querySelector("#blueRGBInput").value=rgb.blue}}else if("convertRGB"==e.target.id){const red=document.querySelector("#redRGBInput").value,green=document.querySelector("#greenRGBInput").value,blue=document.querySelector("#blueRGBInput").value;if(red.length>0&&green.length>0&&blue.length>0){let hex=convertRGBToHex(parseInt(red),parseInt(green),parseInt(blue));document.querySelector("#hexInput").value=hex.toUpperCase()}}});